import { useState } from 'react';
import './ContentCard.css';



const Card = ({ link, children }) => {
  const [isOpen, setIsOpen] = useState(false);

  const handleClick = () => {
    setIsOpen(!isOpen);
    if (!isOpen) {
      window.location.href = link; // Перенаправляем на новую страницу
    }
  };

  return (
    <div className="card" onClick={handleClick} style={{ cursor: 'pointer' }}>
      {children} 
      {isOpen && (
        <div className="card-details" style={{ marginTop: '10px' }}>  
          <p>Дополнительная информация о карточке...</p>
        </div>
      )}
    </div>
  );
};



const ContentCard = ({content}) => {
    const {type, title, description, mediaUrl } = content;

    return(
      <Card link={mediaUrl}>
        <h3>{title}</h3>
      </Card>
    );

    /*return (
        <div className="card">
          <h3>{title}</h3>
          <p>{description}</p>
    
          {type === "video" && (
            <video controls src={mediaUrl} className="content-video" />
          )}
    
          {type === "image" && (
            <img src={mediaUrl} alt={title} className="content-image" />
          )} 
    
          {type === "text" && <p>{description}</p>}
    
          {type === "article" && (
            <a href={mediaUrl} target="_blank" rel="noopener noreferrer">
              Click on me!
            </a>
          )}
        </div>
      );
      */
};

export default ContentCard;
